    export default {
        data() {
            return {
                medicineBarcodeReport: {
                    roleBranchOptionsModel: [],
                    roleAreaOptionsModel: [],
                    patientOptionsModel: [],
                    typeOptionsModel: [],
                    dateRange: ""
                },
                inl: true,
                roleBranchOptions: [],
                roleAreaOptions: [],
                patientOptions: [],
                typeOptions: [],

                exchangeOptions: [],
                dateRange: "",
                activeName: "1",
                medicineBarcodesData: {},
                loading: false,

                companyName: "",
                companyEnName: "",
                addressName: "",

                branchHeader: "All",
                dateRangeHeader: "All",
                pickerOptions2: {
                    shortcuts: [{
                        text: 'Last week',
                        onClick(picker) {
                            const end = new Date();
                            const start = new Date();
                            start.setTime(start.getTime() - 3600 * 1000 * 24 * 7);
                            picker.$emit('pick', [start, end]);
                        }
                    }, {
                        text: 'Last month',
                        onClick(picker) {
                            const end = new Date();
                            const start = new Date();
                            start.setTime(start.getTime() - 3600 * 1000 * 24 * 30);
                            picker.$emit('pick', [start, end]);
                        }
                    }, {
                        text: 'Last 3 months',
                        onClick(picker) {
                            const end = new Date();
                            const start = new Date();
                            start.setTime(start.getTime() - 3600 * 1000 * 24 * 90);
                            picker.$emit('pick', [start, end]);
                        }
                    }]
                },
                printLoading: false,
            }
        },
        methods: {
            fetchBranchOption() {
                Meteor.call("fetchRolesBranch", Meteor.userId(), (err, result) => {
                    this.roleBranchOptions = result;
                })
            },
            fetchAreaOption(val) {
                Meteor.call("fetchRolesAreaByMultiRoleBranch", Meteor.userId(), val, (err, result) => {
                    this.roleAreaOptions = result;
                })
            },
            fetchPatientOption(val) {
                Meteor.call("fetchPatientOption", val, (err, result) => {
                    this.patientOptions = result;
                })
            },
            fetchExchangeOption() {
                Meteor.call("fetchExchangeOption", (err, result) => {
                    this.exchangeOptions = result;
                })
            },
            fetchTypeOption() {
                let list = [];
                list.push({label: "Active", value: "Active"});
                list.push({label: "Partial", value: "Partial"});
                list.push({label: "Complete", value: "Complete"});

                this.typeOptions = list;
            },

            handleRunReport(formName) {

                let params = {};
                let userId = Meteor.userId();

                this.loading = true;
                if (this.medicineBarcodeReport.roleAreaOptionsModel != "") {
                    params.rolesArea = {$in: this.medicineBarcodeReport.roleAreaOptionsModel};

                    Meteor.call("getBranchHeader", this.medicineBarcodeReport.roleAreaOptionsModel, (err, result) => {
                        this.branchHeader = result;
                    })
                }

                if (this.medicineBarcodeReport.dateRange != "") {
                    params.medicineBarcodeDate = {
                        $gte: moment(this.medicineBarcodeReport.dateRange[0]).startOf("days").toDate(),
                        $lte: moment(this.medicineBarcodeReport.dateRange[1]).endOf("days").toDate()
                    };

                    this.dateRangeHeader = moment(this.medicineBarcodeReport.dateRange[0]).format("DD/MM/YYYY") + "-" + moment(this.medicineBarcodeReport.dateRange[1]).format("DD/MM/YYYY");
                }

                if (this.medicineBarcodeReport.patientOptionsModel != "") {
                    params.patientId = {$in: this.medicineBarcodeReport.patientOptionsModel};
                }

                if (this.medicineBarcodeReport.typeOptionsModel != "") {
                    params.status = {$in: this.medicineBarcodeReport.typeOptionsModel};
                }


                Meteor.call('giveMeRegisterReport', params, userId, (err, result) => {
                    if (!err) {
                        this.medicineBarcodesData = result;
                    }
                    this.loading = false;
                });


            },
            getCompany() {
                Meteor.call("getCompany", (err, result) => {
                    if (!err) {
                        this.companyName = result.khName;
                        this.companyEnName = result.enName;
                        this.addressName = result.khAddress;
                    }
                })

            },

            getSummariesRegister(param) {
                const {columns, data} = param;
                const sums = [];
                columns.forEach((column, index) => {
                    if (index === 0) {
                        sums[index] = 'Total';
                        return;
                    }
                    const values = data.map(item => Number(numeral().unformat(item[column.property])));
                    if (!values.every(value => isNaN(value)) && index > 3) {
                        sums[index] = values.reduce((prev, curr) => {
                            const value = Number(curr);
                            if (!isNaN(value)) {
                                return prev + curr;
                            } else {
                                return prev;
                            }
                        }, 0);
                        sums[index] = numeral(sums[index]).format("0,00.000");
                    } else {
                        sums[index] = 'N/A';
                    }
                });

                return sums;
            },
            /* exportToExcel(){
             Meteor.call('giveMeRegisterReport', this.medicineBarcodesData, (err, workbookBuffer) => {
             if (!err) {
             //call mixin saveAs from '/imports/api/mixins/file-saver-fn.js'
             this.saveAs(workbookBuffer, 'RegisterReport');
             }
             })
             },*/
            PrintReport() {
                window.print();
            }
        },
        watch: {


            "medicineBarcodeReport.roleBranchOptionsModel"(val) {
                this.fetchAreaOption(val);
            }
            ,
            "medicineBarcodeReport.roleAreaOptionsModel"(val) {
                this.fetchPatientOption(val);
            }

        },
        created() {
            this.fetchBranchOption();
//            this.fetchPatientOption([]);
            this.fetchTypeOption([]);
            this.getCompany();
            this.medicineBarcodeReport.dateRange = [moment().startOf("months").toDate(), moment().endOf("months").toDate()];


        },
        computed: {
            dataExist() {
                return this.medicineBarcodesData && this.medicineBarcodesData.data && this.medicineBarcodesData.data.length > 0;
            }
        },

    }
