import {Loan_PurposeReact} from "../../collection/clientCode";

export default {
    meteor: {
        langSession() {
            return Session.get('lang') || "en";
        },
        disabledRemove() {
            return Session.get("canRemove");
        },
        disabledUpdate() {
            return Session.get("canUpdate");
        },
        newRe() {
            let vm = this;
            Loan_PurposeReact.find({}).fetch();
            vm.queryData(vm.searchData, vm.skip, vm.currentSize + vm.skip);
        }
    },
    mounted() {
        this.$jQuery('body').off();

    },
    data() {
        return {
            newLoading: true,
            loanPurposeData: [],
            loading: false,
            searchData: '',
            isSearching: false,
            currentPage: 1,
            currentSize: 10,
            count: 0,
            dialogAddLoanPurpose: false,
            dialogUpdateLoanPurpose: false,

            loanPurposeForm: {
                name: "",
                _id: ""
            },
            rules: {
                name: [{required: true, message: 'Please input name', trigger: 'blur'}],
            },
            skip: 0
        }
    },
    watch: {
        currentSize(val) {
            this.isSearching = true;
            this.skip = (this.currentPage - 1) * val;
            this.queryData(this.searchData, this.skip, val + this.skip);
        },
        currentPage(val) {
            this.isSearching = true;
            this.skip = (val - 1) * this.currentSize;
            this.queryData(this.searchData, this.skip, this.currentSize + this.skip);
        },
        searchData(val) {
            this.isSearching = true;
            this.skip = (this.currentPage - 1) * this.currentSize;
            this.queryData(val, this.skip, this.currentSize + this.skip);
        }
    },
    methods: {
        handleSizeChange(val) {
            this.currentSize = val;
        },
        handleCurrentChange(val) {
            this.currentPage = val;
        },
        queryData: _.debounce(function (val, skip, limit) {
            this.newLoading = true;
            Meteor.call('queryLoanPurpose', {
                q: val,
                filter: this.filter,
                options: {skip: skip || 0, limit: limit || 10}
            }, (err, result) => {
                if (!err) {
                    this.loanPurposeData = result.content;
                    this.count = result.countLoanPurpose;
                }
                this.isSearching = false;
                this.newLoading = false;
            });
        }, 300),

        saveLoanPurpose(event) {
            event.preventDefault();

            let vm = this;
            this.$refs["loanPurposeFormAdd"].validate((valid) => {
                if (valid) {
                    let loanPurposeDoc = {
                        name: vm.loanPurposeForm.name,
                        rolesArea: Session.get('area')
                    };
                    Meteor.call("insertLoanPurpose", loanPurposeDoc, (err, result) => {
                        if (!err) {
                            vm.$message({
                                duration: 1000,
                                message: `Save Successfully!`,
                                type: 'success'
                            });
                            vm.dialogAddLoanPurpose = false;

                            vm.$refs["loanPurposeFormAdd"].resetFields();
                        } else {
                            vm.$message({
                                duration: 1000,
                                message: err.message,
                                type: 'error'
                            });
                        }
                    })
                }
            })

        },
        updateLoanPurpose() {
            let vm = this;
            this.$refs["loanPurposeFormUpdate"].validate((valid) => {
                if (valid) {
                    let loanPurposeDoc = {
                        _id: vm.loanPurposeForm._id,
                        name: vm.loanPurposeForm.name,
                        rolesArea: Session.get('area')
                    };

                    Meteor.call("updateLoanPurpose", loanPurposeDoc, (err, result) => {
                        if (!err) {
                            vm.$message({
                                duration: 1000,
                                message: `
                    Update
                    Successfully
                    !`,
                                type: 'success'
                            });
                            vm.dialogUpdateLoanPurpose = false;

                            vm.$refs["loanPurposeFormUpdate"].resetFields();
                        } else {
                            vm.$message({
                                duration: 1000,
                                message: `
                    Update
                    Failed
                    !`,
                                type: 'error'
                            });
                        }
                    })
                }
            })

        },
        removeLoanPurpose(index, row, rows) {
            let vm = this;
            this.$confirm('This will permanently delete the Item. Continue?', 'Warning', {
                confirmButtonText: 'OK',
                cancelButtonText: 'Cancel',
                type: 'warning'
            }).then(() => {
                Meteor.call("removeLoanPurpose", row._id, (err, result) => {
                    if (!err) {
                        rows.splice(index, 1);

                        vm.$message({
                            message: `
                    លុប ${row.code} : ${row.name} បានជោគជ័យ`,
                            type: 'success'
                        });


                    } else {
                        vm.$message({
                            type: 'error',
                            message: 'Delete Fialed'
                        });
                    }

                })

            }).catch(() => {
                this.$message({
                    type: 'info',
                    message: 'Delete canceled'
                });
            });


        },
        findLoanPurposeById(doc) {
            let vm = this;
            vm.loanPurposeForm = {};

            Meteor.call("queryLoanPurposeById", doc.row._id, (err, result) => {
                if (result) {
                    vm.loanPurposeForm._id = result._id;
                    vm.loanPurposeForm = result;
                }
            })
        },
        cancel() {
            this.$message({
                type: 'info',
                message: 'Canceled'
            });
        },
        resetForm() {
            this.loanPurposeForm._id = "";
            if (this.$refs["loanPurposeFormAdd"]) {
                this.$refs["loanPurposeFormAdd"].resetFields();
            }

            if (this.$refs["loanPurposeFormUpdate"]) {
                this.$refs["loanPurposeFormUpdate"].resetFields();
            }

        }
    },
    created() {
        this.isSearching = true;
        this.queryData();
        Meteor.subscribe('Loan_PurposeReact');

    },
    computed: {
        langConfig() {
            let data = compoLang.filter(config => config.lang === this.langSession)[0]['purpose'];
            return data;
        }
    }
}
