import {Co_ClientCodeReact} from "../../collection/clientCode";

export default {
  meteor: {

    newRe() {
      let vm = this;
      Co_ClientCodeReact.find({}).fetch();
      vm.queryData(vm.searchData, vm.skip, vm.currentSize + vm.skip);
    }
  },
  mounted() {
    // this.$jQuery('body').off();
  },
  data() {
    return {
      newLoading: true,
      clientCodeData: [],
      loading: false,
      searchData: '',
      isSearching: false,
      currentPage: 1,
      currentSize: 10,
      count: 0,
      dialogAddLoanPurpose: false,
      dialogUpdateLoanPurpose: false,

      clientCodeForm: {
        name: "",
        _id: ""
      },
      rules: {
        name: [{required: true, message: 'Please input name', trigger: 'blur'}],
      },
      skip: 0
    }
  },
  watch: {
    currentSize(val) {
      this.isSearching = true;
      this.skip = (this.currentPage - 1) * val;
      this.queryData(this.searchData, this.skip, val + this.skip);
    },
    currentPage(val) {
      this.isSearching = true;
      this.skip = (val - 1) * this.currentSize;
      this.queryData(this.searchData, this.skip, this.currentSize + this.skip);
    },
    searchData(val) {
      this.isSearching = true;
      this.skip = (this.currentPage - 1) * this.currentSize;
      this.queryData(val, this.skip, this.currentSize + this.skip);
    }
  },
  methods: {
    handleSizeChange(val) {
      this.currentSize = val;
    },
    handleCurrentChange(val) {
      this.currentPage = val;
    },
    queryData: _.debounce(function (val, skip, limit) {
      this.newLoading = true;
      Meteor.call('queryLoanPurpose', {
        q: val,
        filter: this.filter,
        options: {skip: skip || 0, limit: limit || 10}
      }, (err, result) => {
        if (!err) {
          this.clientCodeData = result.content;
          this.count = result.countLoanPurpose;
        }
        this.isSearching = false;
        this.newLoading = false;
      });
    }, 300),

    saveLoanPurpose(event) {
      event.preventDefault();

      let vm = this;
      this.$refs["clientCodeFormAdd"].validate((valid) => {
        if (valid) {
          let clientCodeDoc = {
            name: vm.clientCodeForm.name,
            rolesArea: Session.get('area')
          };
          Meteor.call("insertLoanPurpose", clientCodeDoc, (err, result) => {
            if (!err) {
              vm.$message({
                duration: 1000,
                message: `Save Successfully!`,
                type: 'success'
              });
              vm.dialogAddLoanPurpose = false;

              vm.$refs["clientCodeFormAdd"].resetFields();
            } else {
              vm.$message({
                duration: 1000,
                message: err.message,
                type: 'error'
              });
            }
          })
        }
      })

    },
    updateLoanPurpose() {
      let vm = this;
      this.$refs["clientCodeFormUpdate"].validate((valid) => {
        if (valid) {
          let clientCodeDoc = {
            _id: vm.clientCodeForm._id,
            name: vm.clientCodeForm.name,
            rolesArea: Session.get('area')
          };

          Meteor.call("updateLoanPurpose", clientCodeDoc, (err, result) => {
            if (!err) {
              vm.$message({
                duration: 1000,
                message: `
                        Update
                        Successfully
                        !`,
                type: 'success'
              });
              vm.dialogUpdateLoanPurpose = false;

              vm.$refs["clientCodeFormUpdate"].resetFields();
            } else {
              vm.$message({
                duration: 1000,
                message: `
                        Update
                        Failed
                        !`,
                type: 'error'
              });
            }
          })
        }
      })

    },
    removeLoanPurpose(index, row, rows) {
      let vm = this;
      this.$confirm('This will permanently delete the Item. Continue?', 'Warning', {
        confirmButtonText: 'OK',
        cancelButtonText: 'Cancel',
        type: 'warning'
      }).then(() => {
        Meteor.call("removeLoanPurpose", row._id, (err, result) => {
          if (!err) {
            rows.splice(index, 1);

            vm.$message({
              message: `
                        លុប ${row.code} : ${row.name} បានជោគជ័យ`,
              type: 'success'
            });


          } else {
            vm.$message({
              type: 'error',
              message: 'Delete Fialed'
            });
          }

        })

      }).catch(() => {
        this.$message({
          type: 'info',
          message: 'Delete canceled'
        });
      });


    },
    findLoanPurposeById(doc) {
      let vm = this;
      vm.clientCodeForm = {};

      Meteor.call("queryLoanPurposeById", doc.row._id, (err, result) => {
        if (result) {
          vm.clientCodeForm._id = result._id;
          vm.clientCodeForm = result;
        }
      })
    },
    cancel() {
      this.$message({
        type: 'info',
        message: 'Canceled'
      });
    },
    resetForm() {
      this.clientCodeForm._id = "";
      if (this.$refs["clientCodeFormAdd"]) {
        this.$refs["clientCodeFormAdd"].resetFields();
      }

      if (this.$refs["clientCodeFormUpdate"]) {
        this.$refs["clientCodeFormUpdate"].resetFields();
      }

    }
  },
  created() {
    this.isSearching = true;
    this.queryData();
    Meteor.subscribe('Loan_PurposeReact');

  },
  computed: {
    langConfig() {
      let data = compoLang.filter(config => config.lang === this.langSession)[0]['purpose'];
      return data;
    }
  }
}
